import React from 'react';
import { Navigate } from 'react-router-dom';
import { useAuth } from '../context/AuthContext';

function ProtectedRoute({ children, adminRequired = false }) {
  const { user, userRole } = useAuth();

  if (!user) {
    return <Navigate to="/login" />;
  }

  if (adminRequired && (!userRole || (userRole.role !== 'super_admin' && userRole.role !== 'restaurant_admin'))) {
    return <Navigate to="/" />;
  }

  // Във вашия ProtectedRoute.jsx
function ProtectedRoute({ children, adminRequired = false }) {
  const { user, loading, isSuperAdmin, isRestaurantAdmin } = useAuth();
  
  console.log('ProtectedRoute - adminRequired:', adminRequired);
  console.log('ProtectedRoute - user:', user);
  console.log('ProtectedRoute - isSuperAdmin:', isSuperAdmin ? isSuperAdmin() : 'undefined');
  console.log('ProtectedRoute - isRestaurantAdmin:', isRestaurantAdmin ? isRestaurantAdmin() : 'undefined');
  
  if (loading) {
    return <div>Зареждане...</div>;
  }
  
  if (!user) {
    return <Navigate to="/login" />;
  }
  
  if (adminRequired) {
    const isAdmin = (isSuperAdmin && isSuperAdmin()) || (isRestaurantAdmin && isRestaurantAdmin());
    console.log('ProtectedRoute - isAdmin:', isAdmin);
    
    if (!isAdmin) {
      return <Navigate to="/" />;
    }
  }
  
  return children;
}
}

export default ProtectedRoute;
