import React, { useEffect, useState } from 'react';
import { useNavigate, useParams } from 'react-router-dom';
import axios from 'axios';

function Events() {
  const { id } = useParams();
  const navigate = useNavigate();
  const [restaurant, setRestaurant] = useState(null);
  const [events, setEvents] = useState([]);
  const [activeTab, setActiveTab] = useState('upcoming');

  useEffect(() => {
    const fetchRestaurant = async () => {
      try {
        const res = await axios.get(`https://reservations-api-production-6b18.up.railway.app/api/restaurants/${id}`);
        setRestaurant(res.data);
      } catch (err) {
        console.error(err);
      }
    };

    const fetchEvents = async () => {
      try {
        const res = await axios.get(`https://reservations-api-production-6b18.up.railway.app/api/restaurants/${id}/events`);
        setEvents(res.data);
      } catch (err) {
        console.error(err);
      }
    };

    fetchRestaurant();
    fetchEvents();
  }, [id]);

  const today = new Date().toISOString().split('T')[0];

  const upcomingEvents = events.filter(event => event.date >= today);
  const pastEvents = events.filter(event => event.date < today);
  const eventsToShow = activeTab === 'upcoming' ? upcomingEvents : pastEvents;

  return (
    <div style={{ maxWidth: '1200px', margin: '0 auto', padding: '20px', color: '#e0e0e0' }}>
      <h1 style={{ textAlign: 'center', marginBottom: '20px' }}>
        {activeTab === 'upcoming' ? 'Предстоящи събития' : 'Минали събития'} в {restaurant?.name}
      </h1>

      <div style={{ display: 'flex', justifyContent: 'center', gap: '20px', marginBottom: '30px' }}>
        <button 
          style={{
            padding: '10px 20px',
            backgroundColor: activeTab === 'upcoming' ? '#e74c3c' : '#333',
            color: '#e0e0e0',
            border: 'none',
            borderRadius: '5px',
            cursor: 'pointer',
            fontSize: '1rem'
          }}
          onClick={() => setActiveTab('upcoming')}
        >
          Предстоящи събития
        </button>
        <button 
          style={{
            padding: '10px 20px',
            backgroundColor: activeTab === 'past' ? '#e74c3c' : '#333',
            color: '#e0e0e0',
            border: 'none',
            borderRadius: '5px',
            cursor: 'pointer',
            fontSize: '1rem'
          }}
          onClick={() => setActiveTab('past')}
        >
          Минали събития
        </button>
      </div>

      <div style={{ display: 'flex', flexDirection: 'column', gap: '30px' }}>
        {eventsToShow.length === 0 ? (
          <div style={{
            textAlign: 'center',
            padding: '30px',
            backgroundColor: '#252525',
            borderRadius: '10px',
            color: '#cccccc'
          }}>
            <p>Няма {activeTab === 'upcoming' ? 'предстоящи' : 'минали'} събития.</p>
          </div>
        ) : (
          eventsToShow.map(event => (
            <div 
              key={event.id} 
              style={{
                backgroundColor: '#252525',
                borderRadius: '10px',
                overflow: 'hidden',
                boxShadow: '0 4px 15px rgba(0,0,0,0.5)'
              }}
            >
              <div style={{
                height: '300px',
                backgroundImage: `url(${event.image})`,
                backgroundSize: 'cover',
                backgroundPosition: 'center'
              }}></div>

              <div style={{ padding: '20px' }}>
                <h2 style={{ marginTop: 0, color: '#e0e0e0' }}>{event.title}</h2>

                <div style={{
                  display: 'flex',
                  gap: '20px',
                  marginBottom: '15px',
                  color: '#aaaaaa',
                  fontSize: '0.9rem'
                }}>
                  <div>Дата: {event.date}</div>
                  <div>Час: {event.time}</div>
                </div>

                <p style={{ marginBottom: '20px', color: '#cccccc' }}>{event.description}</p>

                {activeTab === 'upcoming' && (
                  <>
                    <div style={{
                      backgroundColor: '#333',
                      padding: '15px',
                      borderRadius: '5px',
                      textAlign: 'center',
                      fontStyle: 'italic',
                      color: '#aaaaaa'
                    }}>
                      За повече информация се свържете с нас!
                    </div>

                    <button 
                      style={{
                        marginTop: '15px',
                        padding: '10px 20px',
                        backgroundColor: '#e74c3c',
                        color: 'white',
                        border: 'none',
                        borderRadius: '5px',
                        cursor: 'pointer',
                        fontSize: '1rem',
                        width: '100%'
                      }}
                      onClick={() => alert('Функционалност за резервация за конкретно събитие не е имплементирана')}
                    >
                      Резервирай за това събитие
                    </button>
                  </>
                )}
              </div>
            </div>
          ))
        )}
      </div>

      <div style={{ textAlign: 'center', marginTop: '30px' }}>
        <button 
          style={{
            padding: '10px 20px',
            backgroundColor: '#333',
            color: '#e0e0e0',
            border: 'none',
            borderRadius: '5px',
            cursor: 'pointer',
            fontSize: '1rem'
          }}
          onClick={() => navigate(`/restaurant/${id}`)}
        >
          Назад към ресторанта
        </button>
      </div>
    </div>
  );
}

export default Events;
