import React, { useEffect, useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { createClient } from '@supabase/supabase-js';

// Използвайте същите настройки за Supabase, които използвахте в другите файлове
const supabaseUrl = 'https://lnquyopfvuikhjbjsiqk.supabase.co';
const supabaseKey = 'eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJpc3MiOiJzdXBhYmFzZSIsInJlZiI6ImxucXV5b3BmdnVpa2hqYmpzaXFrIiwicm9sZSI6ImFub24iLCJpYXQiOjE3NDU0ODA5MDEsImV4cCI6MjA2MTA1NjkwMX0.GmZ-5FzZkJyMorLGggF_Jegx2Atn0wRiNKCx4esecJw';
const supabase = createClient(supabaseUrl, supabaseKey);

function SpecialOffers() {
  const { id } = useParams(); // Взима ID на ресторанта от URL
  const navigate = useNavigate();
  const [restaurant, setRestaurant] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  // Примерни данни за специални предложения - в реална ситуация бихте ги заредили от Supabase
  const specialOffers = [
    {
      id: 1,
      title: 'Обедно меню',
      validity: 'Всеки делничен ден от 12:00 до 15:00',
      image: 'https://scontent.fsof8-1.fna.fbcdn.net/v/t39.30808-6/493241996_697534422814815_9080980512401535142_n.jpg?_nc_cat=109&ccb=1-7&_nc_sid=833d8c&_nc_ohc=gJ02SOqWNAAQ7kNvwEEzpMC&_nc_oc=AdlbD5yTZvHSBK96mlWOn2FOSpubOeBpcTglmjrv7aqSXSZYI4zrw9xzMAnw3j9dPd0&_nc_zt=23&_nc_ht=scontent.fsof8-1.fna&_nc_gid=9VyKazvr2U-QNHqFUPgUhw&oh=00_AfFH-jqZG0kZZQ6jo8-rJKjq1TqdUGyYbphVduXkGYA-jQ&oe=6811345B',
      description: 'Изберете между 3 основни ястия, супа на деня и десерт. Включена е и чаша вино или безалкохолна напитка по избор.',
      price: '15.90 лв.'
    },
    {
      id: 2,
      title: 'Семейна вечеря',
      validity: 'Всяка събота и неделя',
      image: 'https://via.placeholder.com/800x400?text=Семейна+вечеря',
      description: 'Специално меню за 4 души, включващо предястия за споделяне, 4 основни ястия, десерти и каничка вино.',
      price: '89.90 лв.'
    },
    {
      id: 3,
      title: 'Щастлив час',
      validity: 'Всеки ден от 17:00 до 19:00',
      image: 'https://via.placeholder.com/800x400?text=Щастлив+час',
      description: 'Всички коктейли и бира на половин цена. Специални мезета на бара.',
      price: 'Различни цени'
    }
  ];

  // Зареждане на данни за ресторанта
  useEffect(() => {
    async function fetchRestaurant() {
      try {
        const { data, error } = await supabase
          .from('restaurants')
          .select('*')
          .eq('id', id)
          .single();
        
        if (error) throw error;
        
        setRestaurant(data);
      } catch (error) {
        console.error("Грешка при зареждане на ресторанта:", error);
        setError("Не можахме да заредим информация за ресторанта");
      } finally {
        setLoading(false);
      }
    }
    
    fetchRestaurant();
  }, [id]);

  if (loading) {
    return <div style={{padding: '20px', textAlign: 'center', color: '#e0e0e0', backgroundColor: '#252525', borderRadius: '10px', margin: '20px auto', maxWidth: '300px'}}>Зареждане...</div>;
  }

  if (error) {
    return <div style={{padding: '20px', textAlign: 'center', color: '#ff3b30', backgroundColor: '#3d1c24', borderRadius: '10px', margin: '20px auto', maxWidth: '600px', border: '1px solid #5c2936'}}>Грешка: {error}</div>;
  }

  return (
    <div style={{maxWidth: '1200px', margin: '0 auto', padding: '20px', color: '#e0e0e0'}}>
      <h1 style={{textAlign: 'center', marginBottom: '20px', color: '#e0e0e0'}}>
        Специални предложения в {restaurant?.name}
      </h1>
      
      <div style={{
        display: 'grid',
        gridTemplateColumns: 'repeat(auto-fill, minmax(350px, 1fr))',
        gap: '30px',
        marginBottom: '30px'
      }}>
        {specialOffers.length === 0 ? (
          <div style={{
            gridColumn: '1 / -1',
            textAlign: 'center',
            padding: '30px',
            backgroundColor: '#252525',
            borderRadius: '10px',
            color: '#aaaaaa'
          }}>
            <p>Няма специални предложения в момента.</p>
          </div>
        ) : (
          specialOffers.map(offer => (
            <div 
              key={offer.id} 
              style={{
                backgroundColor: '#252525',
                borderRadius: '16px',
                overflow: 'hidden',
                boxShadow: '0 8px 16px rgba(0,0,0,0.4)',
                display: 'flex',
                flexDirection: 'column'
              }}
            >
              <div style={{
                height: '200px',
                backgroundImage: `url(${offer.image})`,
                backgroundSize: 'cover',
                backgroundPosition: 'center'
              }}></div>
              
              <div style={{padding: '20px', flex: '1', display: 'flex', flexDirection: 'column', color: '#e0e0e0'}}>
                <h2 style={{marginTop: 0, color: '#e74c3c'}}>{offer.title}</h2>
                
                <div style={{
                  marginBottom: '10px',
                  color: '#aaaaaa',
                  fontSize: '0.9rem'
                }}>
                  <strong>Валидност:</strong> {offer.validity}
                </div>
                
                <p style={{marginBottom: '15px', flex: '1', color: '#cccccc'}}>{offer.description}</p>
                
                <div style={{
                  backgroundColor: '#1e1e1e',
                  padding: '10px',
                  borderRadius: '5px',
                  textAlign: 'center',
                  marginBottom: '15px',
                  color: '#e74c3c',
                  fontWeight: 'bold'
                }}>
                  <strong>Цена:</strong> {offer.price}
                </div>
                
                <div style={{
                  backgroundColor: '#333',
                  padding: '15px',
                  borderRadius: '5px',
                  textAlign: 'center',
                  fontStyle: 'italic',
                  marginTop: 'auto',
                  color: '#aaaaaa',
                  border: '1px solid #444'
                }}>
                  За повече информация се свържете с нас!
                </div>
              </div>
            </div>
          ))
        )}
      </div>
      
      <div style={{textAlign: 'center', marginTop: '30px'}}>
        <button 
          style={{
            padding: '12px 24px',
            backgroundColor: '#e74c3c',
            color: 'white',
            border: 'none',
            borderRadius: '8px',
            cursor: 'pointer',
            fontSize: '1rem',
            boxShadow: '0 4px 6px rgba(0,0,0,0.3)',
          }}
          onClick={() => navigate(`/restaurant/${id}`)}
        >
          Назад към ресторанта
        </button>
      </div>
    </div>
  );
}

export default SpecialOffers;
