// Модифицирайте TestAdmin компонента
import React from 'react';
import { useAuth } from '../context/AuthContext';

function TestAdmin() {
  const auth = useAuth();
  
  // Извличаме всички данни от контекста
  const { 
    user, 
    userRole, 
    isSuperAdmin, 
    isRestaurantAdmin,
    supabase
  } = auth;
  
  // Проверка на методите
  const isSuperAdminResult = typeof isSuperAdmin === 'function' ? isSuperAdmin() : 'не е функция';
  const isRestaurantAdminResult = typeof isRestaurantAdmin === 'function' ? isRestaurantAdmin() : 'не е функция';
  
  // Опит за извличане на роли от базата данни
  const checkRoles = async () => {
    if (!user || !supabase) return;
    
    try {
      const { data, error } = await supabase
        .from('user_roles')
        .select('*')
        .eq('user_id', user.id);
      
      if (error) throw error;
      
      alert(`Намерени роли: ${JSON.stringify(data)}`);
    } catch (err) {
      alert(`Грешка при проверка на роли: ${err.message}`);
    }
  };
  
  return (
    <div style={{ padding: '20px', maxWidth: '800px', margin: '0 auto' }}>
      <h1>Тестов административен панел</h1>
      
      <div style={{ marginBottom: '20px', padding: '10px', border: '1px solid #ccc' }}>
        <h2>Информация за потребителя:</h2>
        <pre>{JSON.stringify(user, null, 2)}</pre>
      </div>
      
      <div style={{ marginBottom: '20px', padding: '10px', border: '1px solid #ccc' }}>
        <h2>Информация за ролята:</h2>
        <pre>{JSON.stringify(userRole, null, 2)}</pre>
      </div>
      
      <div style={{ marginBottom: '20px', padding: '10px', border: '1px solid #ccc' }}>
        <h2>Административни права:</h2>
        <p><strong>isSuperAdmin:</strong> {String(isSuperAdminResult)}</p>
        <p><strong>isRestaurantAdmin:</strong> {String(isRestaurantAdminResult)}</p>
      </div>
      
      <button 
        onClick={checkRoles}
        style={{ padding: '10px', background: '#3498db', color: 'white', border: 'none', borderRadius: '5px' }}
      >
        Проверка на роли в базата данни
      </button>
      
      <div style={{ marginTop: '20px' }}>
        <a href="/admin" style={{ display: 'block', marginBottom: '10px' }}>Към админ панела</a>
        <a href="/" style={{ display: 'block' }}>Към начална страница</a>
      </div>
    </div>
  );
}

export default TestAdmin;
