import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { supabase } from '../utils/supabase';
import { useAuth } from '../context/AuthContext';
import '../styles/Admin.css';

function AdminDashboard() {
  const { user, userRole, isSuperAdmin, isRestaurantAdmin, signOut } = useAuth();
  const [section, setSection] = useState('dashboard');
  const [restaurants, setRestaurants] = useState([]);
  const [selectedRestaurant, setSelectedRestaurant] = useState(null);
  const [loading, setLoading] = useState(true);
  const navigate = useNavigate();

  useEffect(() => {
    const fetchData = async () => {
      try {
        setLoading(true);
        
        if (!user) {
          navigate('/login');
          return;
        }
        
        // Проверка дали потребителят има админ права
        if (!isSuperAdmin() && !isRestaurantAdmin()) {
          navigate('/');
          return;
        }
        
        // Зареждане на ресторантите
        let restaurantsQuery = supabase.from('restaurants').select('*');
        
        // Ако е ресторантски админ, филтрираме само за неговия ресторант
        if (isRestaurantAdmin()) {
          restaurantsQuery = restaurantsQuery.eq('id', userRole.restaurant_id);
        }
        
        const { data: restaurantsData, error: restaurantsError } = await restaurantsQuery;
        
        if (restaurantsError) throw restaurantsError;
        
        setRestaurants(restaurantsData);
        
        // Избиране на ресторант по подразбиране
        if (restaurantsData.length > 0) {
          if (isRestaurantAdmin()) {
            setSelectedRestaurant(restaurantsData[0]);
          } else if (isSuperAdmin()) {
            setSelectedRestaurant(restaurantsData[0]);
          }
        }
      } catch (error) {
        console.error('Error loading admin data:', error);
      } finally {
        setLoading(false);
      }
    };
    
    fetchData();
  }, [user, userRole, isSuperAdmin, isRestaurantAdmin, navigate]);

  const handleSignOut = async () => {
    await signOut();
    navigate('/login');
  };

  if (loading) {
    return <div className="admin-loading">Зареждане на администраторски панел...</div>;
  }

  return (
    <div className="admin-container">
      <div className="admin-sidebar">
        <div className="admin-sidebar-header">
          <h2>Админ панел</h2>
          <p>{isSuperAdmin() ? 'Главен администратор' : 'Ресторантски администратор'}</p>
          
          {isSuperAdmin() && restaurants.length > 0 && (
            <div className="restaurant-selector">
              <label>Избран ресторант:</label>
              <select 
                value={selectedRestaurant?.id || ''} 
                onChange={(e) => {
                  const selected = restaurants.find(r => r.id === e.target.value);
                  setSelectedRestaurant(selected);
                }}
              >
                {restaurants.map((restaurant) => (
                  <option key={restaurant.id} value={restaurant.id}>
                    {restaurant.name}
                  </option>
                ))}
              </select>
            </div>
          )}
        </div>
        
        <ul className="admin-menu">
          <li 
            className={section === 'dashboard' ? 'active' : ''}
            onClick={() => setSection('dashboard')}
          >
            Табло
          </li>
          
          <li 
            className={section === 'reservations' ? 'active' : ''}
            onClick={() => setSection('reservations')}
          >
            Резервации
          </li>
          
          <li 
            className={section === 'menu' ? 'active' : ''}
            onClick={() => setSection('menu')}
          >
            Меню
          </li>
          
          <li 
            className={section === 'events' ? 'active' : ''}
            onClick={() => setSection('events')}
          >
            Събития
          </li>
          
          <li 
            className={section === 'special_offers' ? 'active' : ''}
            onClick={() => setSection('special_offers')}
          >
            Специални предложения
          </li>
          
          <li 
            className={section === 'tables' ? 'active' : ''}
            onClick={() => setSection('tables')}
          >
            Управление на маси
          </li>
          
          {isSuperAdmin() && (
            <li 
              className={section === 'admins' ? 'active' : ''}
              onClick={() => setSection('admins')}
            >
              Администратори
            </li>
          )}
          
          <li onClick={handleSignOut}>Изход</li>
        </ul>
      </div>
      
      <div className="admin-content">
        {section === 'dashboard' && (
          <DashboardSection restaurant={selectedRestaurant} />
        )}
        
        {section === 'reservations' && (
          <ReservationsSection restaurant={selectedRestaurant} />
        )}
        
        {section === 'menu' && (
          <MenuSection restaurant={selectedRestaurant} />
        )}
        
        {section === 'events' && (
          <EventsSection restaurant={selectedRestaurant} />
        )}
        
        {section === 'special_offers' && (
          <SpecialOffersSection restaurant={selectedRestaurant} />
        )}
        
        {section === 'tables' && (
          <TablesSection restaurant={selectedRestaurant} />
        )}
        
        {section === 'admins' && isSuperAdmin() && (
          <AdminsSection restaurants={restaurants} />
        )}
      </div>
    </div>
  );
}

// Подсекции за административния панел
function DashboardSection({ restaurant }) {
  return (
    <div>
      <h1>Табло: {restaurant?.name}</h1>
      <div className="admin-stats-grid">
        <div className="admin-stat-card">
          <h3>Резервации днес</h3>
          <p className="stat">12</p>
        </div>
        
        <div className="admin-stat-card">
          <h3>Активни маси</h3>
          <p className="stat">8/24</p>
        </div>
        
        <div className="admin-stat-card">
          <h3>Предстоящи събития</h3>
          <p className="stat">3</p>
        </div>
      </div>
    </div>
  );
}

function ReservationsSection({ restaurant }) {
  // Пример със статични данни - заменете с данни от Supabase
  const reservations = [
    { id: 1, date: '24.04.2025', time: '19:00', table: 12, name: 'Иван Иванов', phone: '088 888 8888', guests: 4, status: 'Потвърдена' },
    { id: 2, date: '24.04.2025', time: '20:30', table: 8, name: 'Петър Петров', phone: '087 777 7777', guests: 2, status: 'Чакаща' }
  ];
  
  return (
    <div>
      <h1>Резервации: {restaurant?.name}</h1>
      
      <div className="admin-table-container">
        <table className="admin-table">
          <thead>
            <tr>
              <th>Дата</th>
              <th>Час</th>
              <th>Маса</th>
              <th>Клиент</th>
              <th>Телефон</th>
              <th>Гости</th>
              <th>Статус</th>
              <th>Действия</th>
            </tr>
          </thead>
          <tbody>
            {reservations.map(reservation => (
              <tr key={reservation.id}>
                <td>{reservation.date}</td>
                <td>{reservation.time}</td>
                <td>{reservation.table}</td>
                <td>{reservation.name}</td>
                <td>{reservation.phone}</td>
                <td>{reservation.guests}</td>
                <td>{reservation.status}</td>
                <td>
                  <button className="admin-action-btn edit">Редактирай</button>
                  <button className="admin-action-btn delete">Изтрий</button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
}

// Добавете останалите подсекции по подобен начин

function MenuSection({ restaurant }) {
  // Имплементирайте управление на менюто
  return <h1>Меню: {restaurant?.name}</h1>;
}

function EventsSection({ restaurant }) {
  // Имплементирайте управление на събитията
  return <h1>Събития: {restaurant?.name}</h1>;
}

function SpecialOffersSection({ restaurant }) {
  // Имплементирайте управление на специалните предложения
  return <h1>Специални предложения: {restaurant?.name}</h1>;
}

function TablesSection({ restaurant }) {
  // Имплементирайте управление на масите
  return <h1>Управление на маси: {restaurant?.name}</h1>;
}

function AdminsSection({ restaurants }) {
  // Имплементирайте управление на администраторите
  return (
    <div>
      <h1>Управление на администратори</h1>
      
      <button className="admin-add-btn">Добави нов администратор</button>
      
      <div className="admin-table-container">
        <table className="admin-table">
          <thead>
            <tr>
              <th>Email</th>
              <th>Име</th>
              <th>Роля</th>
              <th>Ресторант</th>
              <th>Действия</th>
            </tr>
          </thead>
          <tbody>
            <tr>
              <td>admin@example.com</td>
              <td>Главен Администратор</td>
              <td>Главен администратор</td>
              <td>Всички</td>
              <td>
                <button className="admin-action-btn edit">Редактирай</button>
                <button className="admin-action-btn delete">Изтрий</button>
              </td>
            </tr>
            <tr>
              <td>manager@example.com</td>
              <td>Ресторант Мениджър</td>
              <td>Ресторантски администратор</td>
              <td>Bellezza Cafe Bar</td>
              <td>
                <button className="admin-action-btn edit">Редактирай</button>
                <button className="admin-action-btn delete">Изтрий</button>
              </td>
            </tr>
          </tbody>
        </table>
      </div>
    </div>
  );
}

export default AdminDashboard;
