import React from 'react';
import { Navigate } from 'react-router-dom';
import { useAuth } from '../context/AuthContext';

function ProtectedRoute({ children, adminRequired = false }) {
  const { user, userRole, loading, isSuperAdmin, isRestaurantAdmin } = useAuth();
  
  console.log('ProtectedRoute - adminRequired:', adminRequired);
  console.log('ProtectedRoute - user:', user);
  console.log('ProtectedRoute - userRole:', userRole);
  console.log('ProtectedRoute - isSuperAdmin:', isSuperAdmin ? isSuperAdmin() : 'undefined');
  console.log('ProtectedRoute - isRestaurantAdmin:', isRestaurantAdmin ? isRestaurantAdmin() : 'undefined');
  
  if (loading) {
    return <div>Зареждане...</div>;
  }
  
  if (!user) {
    return <Navigate to="/login" />;
  }
  
  if (adminRequired) {
    // Проверка по два начина - чрез функциите и директно чрез userRole
    const isAdminByFunctions = (isSuperAdmin && isSuperAdmin()) || (isRestaurantAdmin && isRestaurantAdmin());
    const isAdminByRole = userRole && (userRole.role === 'super_admin' || userRole.role === 'restaurant_admin');
    
    console.log('ProtectedRoute - isAdminByFunctions:', isAdminByFunctions);
    console.log('ProtectedRoute - isAdminByRole:', isAdminByRole);
    
    if (!isAdminByFunctions && !isAdminByRole) {
      return <Navigate to="/" />;
    }
  }
  
  return children;
}

export default ProtectedRoute;
